<?phpnamespace Datos\Model; class Datos {     public $id;     public $nombre;     public $foto;     public $edad;     public function exchangeArray($data)     {         $this->id     = (!empty($data['id'])) ? $data['id'] : null;         $this->nombre = (!empty($data['nombre'])) ? $data['nombre'] : null;         $this->foto  = (!empty($data['foto'])) ? $data['foto'] : null;         $this->edad  = (!empty($data['edad'])) ? $data['edad'] : null;     } }